/* eslint-disable jsx-a11y/no-distracting-elements */
/* eslint-disable jsx-a11y/img-redundant-alt */
import React from 'react';
import "./marquee.css";
import ReactLogo from '../images/ReactLogo.png';
import BootstrapLogo from '../images/BootstrapLogo.png';
import CSSLogo from '../images/CSSLogo.png';
import HTMLLogo from '../images/HTMLLogo.png';

export default function Marquee() {
  return (
    <div className="marquee">
      <div className="title">
        <h2>Using Properties:</h2>
      </div>
        <marquee direction="left" speed={100} delay={5}>
          <div className="image_wrapper">
          <img src={ReactLogo} alt="Loading: Please wait for Image" />
          </div>
          <div className="image_wrapper">
          <img src={BootstrapLogo} alt="Loading: Please wait for Image" />
          </div>
          <div className="image_wrapper">
          <img src={CSSLogo} alt="Loading: Please wait for Image" />
          </div>
          <div className="image_wrapper">
          <img src={HTMLLogo} alt="Loading: Please wait for Image" />
          </div>
          <div className="image_wrapper">
          <img src={ReactLogo} alt="Loading: Please wait for Image" />
          </div>
        </marquee>
    </div>
  )
}
